from nonebot import get_driver

from .config import Config

from nonebot import on_command
from nonebot.adapters import Message

global_config = get_driver().config
config = Config.parse_obj(global_config)

replay = on_command('replay', aliases={'replay'},priority=5)

@replay.handle()
async def handle_replay(bot,event):
    message = event.get_message()
    if not message:
        await replay.finish("请在/replay后面空一格并跟上需要重复的信息")
    content = message.extract_plain_text().strip()
    replay_content = content[8:]
    await replay.finish(replay_content)